mapper.select:
SELECT 

FROM bu_member_info a

WHERE a.id = #{id}

SELECT 

FROM bu_member_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.mobile_phone = #{mobilePhone}


		AND a.name = #{name}


		AND a.contact = #{contact}


		AND a.create_date >= #{createDate}


		AND a.wx_status = #{wxStatus}


		AND a.zfb_status = #{zfbStatus}


		AND a.pay_code = #{payCode}


		AND (a.create_date between str_to_date(DATE_FORMAT(#{createDate},'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s')
		AND DATE_ADD(str_to_date(DATE_FORMAT(#{createDate},'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s'),INTERVAL 1 DAY))


		AND a.status = #{status}



		AND (o.id=#{office.id} or o.parent_ids LIKE 
			'%'||#{office.id}||'%')
			'%'+#{office.id}+'%')
			concat('%',#{office.id},'%'))






		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM bu_member_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM bu_member_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE bu_member_info a, bu_member_bank b SET mobile_phone = #{mobilePhone}, a.name = #{name}, a.short_name = #{shortName}, a.contact = #{contact}, a.status = #{status}, a.cert_nbr = #{certNbr}, a.cert_pic1 = #{certPic1}, a.cert_pic2 = #{certPic2}, a.addr = #{addr}, a.wx_route_id = #{wxRouteId}, a.zfb_route_id = #{zfbRouteId},
a.short_name = #{shortName}, a.mobile_phone = #{mobilePhone}, a.t0_draw_fee = #{t0DrawFee}, a.t0_trade_rate = #{t0TradeRate}, a.t1_draw_fee = #{t1DrawFee}, a.t1_trade_rate = #{t1TradeRate},
b.settle_type = #{memberBank.settleType}, b.account_number = #{memberBank.accountNumber}, b.bank_open = #{memberBank.bankOpen},
a.update_by = #{updateBy.id}, a.update_date = #{updateDate}, b.bank_open = #{memberBank.bankOpen}, b.account_number = #{memberBank.accountNumber}, a.level = #{level}, a.draw_status =#{drawStatus}, a.single_limit =#{singleLimit}, a.day_limit =#{dayLimit}, a.bus_pic =#{busPic}, a.head_pic =#{headPic}, a.desk_pic =#{deskPic}, a.inside_pic =#{insidePic}, a.staff_pic =#{staffPic}
WHERE a.id = b.member_id and a.id = #{id}

UPDATE bu_member_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT id
FROM bu_member_info a
WHERE a.code = #{code}

select a.tx_month as "txMonth", a.num as "num" from rpt_month_member a
a.tx_month >= #{beginMonth} and a.tx_month <= #{endMonth}
ORDER BY ${page.orderBy}
ORDER BY a.tx_month ASC

SELECT 
	,
	ba.name AS "bindBankUsername",
	ba.bank_name AS "bindBankName",
	ba.acc AS "bindBankNumber",
	ba.mobile_phone AS "bindBankPhone",
	ba.create_date AS "bindBankTime"
FROM bu_member_info a

right join bu_member_bind_acc ba on ba.member_id = a.id

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.mobile_phone = #{mobilePhone}


		AND a.name = #{name}



		AND left(ba.create_date,19) >= #{beginBindBankTime}


		AND left(ba.create_date,19) <= #{endBindBankTime}


		AND (o.id=#{office.id} or o.parent_ids LIKE 
			'%'||#{office.id}||'%')
			'%'+#{office.id}+'%')
			concat('%',#{office.id},'%'))






		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE bu_member_info SET 
	status = 1
WHERE id = #{id}
