WHERE a.id = #{id}

SELECT 

FROM bu_debit_note a


	1=1

		AND b.code = #{memberCode}


	  	AND a.order_code LIKE CONCAT('%', #{orderCode},'%') 


		AND a.txn_type = #{txnType}


	  	AND a.order_num_outer LIKE CONCAT('%', #{orderNumOuter},'%') 


		AND a.settle_type = #{settleType}



		AND a.create_date >= #{beginTime}


		AND a.create_date <= #{endTime}






				 	(AND a.status <>'0' AND AND a.status <>'1' AND a.status <>'3')


				AND a.status = #{respType}





		AND (o.id=#{office.id} or o.parent_ids LIKE 
			'%'||#{office.id}||'%')
			'%'+#{office.id}+'%')
			concat('%',#{office.id},'%'))


		AND b.mobile_phone = #{mobilePhone}


		AND b.name = #{memberName}






		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC
