mapper.select:
SELECT 

FROM v_trade_detail a

WHERE a.id = #{id}

	SELECT 

	FROM (SELECT

		FROM
			`bu_trade_detail` `d`
		where 
		d.del_flag = #{DEL_FLAG_NORMAL}

			AND d.txn_date >= #{beginTime}


			AND d.txn_date <= #{endTime}

		UNION ALL
		SELECT

		FROM
			`ml_trade_detail` `e`
		where
		e.del_flag = #{DEL_FLAG_NORMAL}

			AND e.txn_date >= #{beginTime}


			AND e.txn_date <= #{endTime}

     	) a


		1=1

			AND b.code = #{memberCode}


			AND a.order_code = #{orderCode}


			AND a.txn_type = #{txnType}


			AND a.pt_serial_no = #{ptSerialNo}


			AND a.settle_type = #{settleType}




			AND a.resp_type = #{respType}


			AND a.order_code = #{orderCode}


			AND a.balance_date >= #{balanceStartTime}


			AND a.balance_date <= #{balanceEndTime}



			AND (o.id=#{office.id} or o.parent_ids LIKE 
				'%'||#{office.id}||'%')
				'%'+#{office.id}+'%')
				concat('%',#{office.id},'%'))


			AND b.mobile_phone = #{mobilePhone}


			AND b.name = #{memberName}






			ORDER BY ${page.orderBy}


			ORDER BY a.resp_date DESC

	SELECT

	FROM  (SELECT

		FROM
			`bu_trade_detail_his` `d`
		where 
		d.del_flag = #{DEL_FLAG_NORMAL}

			AND d.txn_date >= #{beginTime}


			AND d.txn_date <= #{endTime}

		UNION ALL
		SELECT

		FROM
			`ml_trade_detail_his` `e`
		where
		e.del_flag = #{DEL_FLAG_NORMAL}

			AND e.txn_date >= #{beginTime}


			AND e.txn_date <= #{endTime}

     	) a


		a.del_flag = #{DEL_FLAG_NORMAL}

			AND b.code = #{memberCode}


			AND a.order_code = #{orderCode}


			AND a.txn_type = #{txnType}


			AND a.pt_serial_no = #{ptSerialNo}


			AND a.settle_type = #{settleType}



			AND a.txn_date >= #{beginTime}


			AND a.txn_date <= #{endTime}


			AND a.resp_type = #{respType}


			AND a.order_code = #{orderCode}


			AND a.balance_date >= #{balanceStartTime}


			AND a.balance_date <= #{balanceEndTime}



			AND (o.id=#{office.id} or o.parent_ids LIKE
				'%'||#{office.id}||'%')
				'%'+#{office.id}+'%')
				concat('%',#{office.id},'%'))


			AND b.mobile_phone = #{mobilePhone}


			AND b.name = #{memberName}






			ORDER BY ${page.orderBy}


			ORDER BY a.resp_date DESC

select a.tx_month as "txMonth", a.wx_money as "wxMoney", a.qq_money as "qqMoney", a.zfb_money as "zfbMoney",
a.bouns_quick_money as "bounsQuickMoney", a.quick_money as "quickMoney",
a.all_money as "allMoney", a.jd_money as "jdMoney", a.bd_money as "bdMoney" from rpt_month_trade a
a.tx_month >= #{beginMonth} and a.tx_month <= #{endMonth}
ORDER BY ${page.orderBy}
ORDER BY a.tx_month ASC

    select  

    from v_trade_detail a


    	a.del_flag = #{DEL_FLAG_NORMAL}
    	AND (a.txn_type = 6 OR a.txn_type = 7)

			AND b.code = #{memberCode}


			AND a.txn_type = #{txnType}



			AND a.txn_date >= #{beginTime}


			AND a.txn_date <= #{endTime}


			AND b.name = #{memberName}




			ORDER BY ${page.orderBy}


			ORDER BY a.req_date DESC
