mapper.select:
SELECT t.member_id AS memberId,t.txn_date AS txnDate, t.route_code AS routeCode, t.merchant_code AS merchantCode,t.txn_method AS txnMethod,t.txn_type AS txnType, convert(t.trade_money,decimal(10,2)) AS tradeMoney,(t.agent_cost-t.plat_cost) AS platProfit, (t.agent_cost-t.real_plat_cost) AS realPlatProfit,(t.member_cost-t.agent_cost) AS agentProfit , (t.agent_cost_level2 - t.agent_cost) AS agentProfitLevel1,(t.real_plat_cost - t.plat_cost) AS channelProfit, t.plat_cost AS platCost,t.real_plat_cost AS realPlatCost,t.member_cost AS memberCost, CASE WHEN t.route_code='1038' THEN 0 ELSE (t.trade_money-t.member_cost) END AS settleMoney, convert(t.plat_trade_rate,decimal(10,4)) AS platTradeRate,convert(t.real_plat_trade_rate,decimal(10,4)) AS realPlatTradeRate,convert(t.agent_trade_rate,decimal(10,4)) AS agentTradeRate, convert(t.agent_trade_rate_level2,decimal(10,4)) AS agentTradeRateLevel2, convert(t.member_trade_rate,decimal(10,4)) AS memberTradeRate, convert((t.agent_trade_rate-t.plat_trade_rate),decimal(10,4)) AS platProfitRate, convert((t.agent_trade_rate-t.real_plat_trade_rate),decimal(10,4)) AS realPlatProfitRate, convert((t.member_trade_rate-t.agent_trade_rate),decimal(10,4)) AS agentProfitRate , CONVERT ((t.agent_trade_rate_level2 - t.agent_trade_rate),DECIMAL (10, 4)) AS agentProfitRateLevel1, CONVERT ((t.real_plat_trade_rate - t.plat_trade_rate),DECIMAL (10, 4)) AS channelProfitRate, m.`name` AS memberName,m.`code` AS memberCode,o.`name` AS "office.name",o1.`name` AS agentName FROM bu_trade_profit t LEFT JOIN bu_member_info m ON m.id=t.member_id LEFT JOIN bu_epay_code c ON c.pay_code = m.pay_code LEFT JOIN sys_office o ON o.id = c.office_id LEFT JOIN sys_office o1 ON o1.id = t.agent_office_id WHERE 1=1
AND m.code = #{memberCode}
AND t.txn_date>= #{beginTime}
AND t.txn_date <= #{endTime}
AND t.merchant_code = #{merchantCode}
AND t.txn_type = #{txnType}
AND t.txn_method = #{txnMethod}
AND t.route_code = #{routeCode}
AND (o.id=#{office.id} or o.parent_ids LIKE '%'||#{office.id}||'%') '%'+#{office.id}+'%') concat('%',#{office.id},'%'))
AND m.name LIKE concat('%',#{memberName},'%')
ORDER BY t.txn_date DESC,t.member_id DESC

SELECT convert(SUM(t.trade_money),decimal(10,2)) AS tradeMoney, convert(SUM(t.agent_cost-t.plat_cost),decimal(10,2)) AS platProfit, convert(SUM(t.member_cost-t.agent_cost),decimal(10,2)) AS agentProfit , convert(SUM(t.plat_cost),decimal(10,2)) AS platCost, convert(SUM(t.member_cost),decimal(10,2)) AS memberCost, convert(SUM(t.trade_money-t.member_cost),decimal(10,2)) AS settleMoney, convert(SUM(t.agent_cost-t.real_plat_cost),decimal(10,2)) AS realPlatProfit, convert(SUM(t.real_plat_cost),decimal(10,2)) AS realPlatCost, convert(SUM(t.agent_cost_level2-t.agent_cost),decimal(10,2)) AS agentProfitLevel1 FROM bu_trade_profit t LEFT JOIN bu_member_info m ON m.id=t.member_id LEFT JOIN bu_epay_code c ON c.pay_code = m.pay_code LEFT JOIN sys_office o ON o.id = c.office_id WHERE 1=1
AND m.code = #{memberCode}
AND t.txn_date>= #{beginTime}
AND t.txn_date <= #{endTime}
AND t.merchant_code = #{merchantCode}
AND t.txn_type = #{txnType}
AND t.txn_method = #{txnMethod}
AND t.route_code = #{routeCode}
AND (o.id=#{office.id} or o.parent_ids LIKE '%'||#{office.id}||'%') '%'+#{office.id}+'%') concat('%',#{office.id},'%'))
AND m.name LIKE concat('%',#{memberName},'%')

SELECT a.member_id AS memberId,a.txn_date AS txnDate, a.route_code AS routeCode,a.agent_office_id AS agentOfficeId, convert(a.trade_money,decimal(10,2)) AS tradeMoney,a.agent_cost AS agentCost,a.agent_profit AS agentProfit, a.draw_per*100 drawPer,m.`name` AS memberName,m.`code` AS memberCode,s.`name` AS agentName ,o.`name` AS "office.name" FROM( SELECT t.member_id,t.txn_date, t.route_code,t.agent_office_id, sum(t.trade_money) AS trade_money,SUM(t.agent_cost) AS agent_cost,SUM(t.agent_profit) AS agent_profit,t.draw_per FROM bu_trade_profit t where 1=1
AND t.txn_date>= #{beginTime}
AND t.txn_date <= #{endTime}
AND t.route_code = #{routeCode}
AND t.agent_office_id = #{agentOfficeId}
GROUP BY t.txn_date,t.route_code,t.member_id) a LEFT JOIN sys_office s ON s.id=a.agent_office_id LEFT JOIN bu_member_info m ON m.id=a.member_id LEFT JOIN bu_epay_code c ON c.pay_code = m.pay_code LEFT JOIN sys_office o ON o.id = c.office_id where 1=1
AND m.code = #{memberCode}
AND m.name LIKE concat('%',#{memberName},'%')
AND s.name LIKE concat('%',#{agentName},'%')
ORDER BY a.txn_date DESC,a.agent_office_id DESC,a.member_id DESC

SELECT convert(SUM(a.trade_money),decimal(10,2)) AS tradeMoney, convert(SUM(a.agent_cost),decimal(10,2)) AS agentCost, convert(SUM(a.agent_profit),decimal(10,2)) AS agentProfit FROM( SELECT t.member_id,t.txn_date, t.route_code,t.agent_office_id, sum(t.trade_money) AS trade_money,SUM(t.agent_cost) AS agent_cost,SUM(t.agent_profit) AS agent_profit,t.draw_per FROM bu_trade_profit t where 1=1
AND t.txn_date>= #{beginTime}
AND t.txn_date <= #{endTime}
AND t.route_code = #{routeCode}
AND t.agent_office_id = #{agentOfficeId}
GROUP BY t.txn_date,t.route_code,t.member_id) a LEFT JOIN sys_office s ON s.id=a.agent_office_id LEFT JOIN bu_member_info m ON m.id=a.member_id LEFT JOIN bu_epay_code c ON c.pay_code = m.pay_code LEFT JOIN sys_office o ON o.id = c.office_id where 1=1
AND m.code = #{memberCode}
AND m.name LIKE concat('%',#{memberName},'%')
AND s.name LIKE concat('%',#{agentName},'%')

SELECT a.txn_date AS txnDate, a.route_code AS routeCode,a.agent_office_id AS agentOfficeId, convert(a.trade_money,decimal(10,2)) AS tradeMoney,a.agent_cost AS agentCost,a.agent_profit AS agentProfit, a.draw_per*100 drawPer,s.`name` AS agentName FROM( SELECT t.txn_date, t.route_code,t.agent_office_id, sum(t.trade_money) AS trade_money,SUM(t.agent_cost) AS agent_cost,SUM(t.agent_profit) AS agent_profit,t.draw_per FROM bu_trade_profit t where 1=1
AND t.txn_date>= #{beginTime}
AND t.txn_date <= #{endTime}
AND t.route_code = #{routeCode}
AND t.agent_office_id = #{agentOfficeId}
GROUP BY t.txn_date,t.route_code,t.agent_office_id) a LEFT JOIN sys_office s ON s.id=a.agent_office_id where 1=1
AND s.name LIKE concat('%',#{agentName},'%')
ORDER BY a.txn_date DESC,a.agent_office_id DESC

SELECT t.member_id AS memberId,t.txn_date AS txnDate, t.route_code AS routeCode, t.agent_office_id AS agentOfficeId,convert(t.money,decimal(10,2)) AS money,convert(t.profit,decimal(10,2)) AS profit, m.`name` AS memberName,m.`code` AS memberCode,o.`name` AS "office.name",o1.`name` AS agentName FROM bu_routeway_draw_profit t LEFT JOIN bu_member_info m ON m.id=t.member_id LEFT JOIN bu_epay_code c ON c.pay_code = m.pay_code LEFT JOIN sys_office o ON o.id = c.office_id LEFT JOIN sys_office o1 ON o1.id = t.agent_office_id WHERE 1=1
AND m.code = #{memberCode}
AND t.txn_date>= #{beginTime}
AND t.txn_date <= #{endTime}
AND t.route_code = #{routeCode}
AND (o.id=#{office.id} or o.parent_ids LIKE '%'||#{office.id}||'%') '%'+#{office.id}+'%') concat('%',#{office.id},'%'))
AND m.name LIKE concat('%',#{memberName},'%')
ORDER BY t.txn_date DESC,t.member_id DESC

